mapper.select:
	SELECT COUNT(DISTINCT L.L_NO)
	FROM SEARCH_DETAIL L
	JOIN LESSON LS ON(L.L_NO = LS.L_NO)
	
		 L.L_STATUS = 'Y'
		 AND L.START_TIME >= SYSDATE AND LS.REMAIN !=0
		
			 AND L.L_DAY IN 
			 
            #{item}
        	 
		
		
			AND (L.L_TIME LIKE 
			 	 
            '%' || #{item} || '%'      
        	)
		
		
			AND L.TTYPE = #{tType}
		
		
			AND L.L_REGION LIKE '%' || #{regionMain} || '%'
		
		
			AND L.L_REGION_SUB = #{regionSub}
		
		
			AND ((L.L_TITLE LIKE '%' || #{sValue} || '%') OR (L.L_INTRODUCTION LIKE  '%' || #{sValue} || '%'))
		
		
			AND L.L_CATE_MAIN = #{cateMain}
		
		
			AND L.L_CATE_SUB = #{cateSub}

	SELECT DISTINCT L.L_NO
	FROM SEARCH_DETAIL L
	JOIN LESSON LS ON(L.L_NO = LS.L_NO)
	
		 L.L_STATUS = 'Y'
		 AND L.START_TIME >= SYSDATE AND LS.REMAIN !=0
		
			 AND L.L_DAY IN 
			 
            #{item}
        	 
		
		
			AND (L.L_TIME LIKE 
			 	 
            '%' || #{item} || '%'      
        	)
		
		
			AND L.TTYPE = #{tType}
		
		
			AND L.L_REGION LIKE '%' || #{regionMain} || '%' 
		
		
			AND L.L_REGION_SUB = #{regionSub}
		
		
			AND ((L.L_TITLE LIKE '%' || #{sValue} || '%') OR (L.L_INTRODUCTION LIKE  '%' || #{sValue} || '%'))
		
		
			AND L.L_CATE_MAIN = #{cateMain}
		
		
			AND L.L_CATE_SUB = #{cateSub}

SELECT L_REGION as location, COUNT(*) as count, 'RegionMain' as lType FROM LESSON_INFO WHERE L_NO IN ( SELECT L.L_NO FROM LESSON_INFO L JOIN LESSON LS ON(L.L_NO = LS.L_NO) JOIN TUTOR T ON(L.USER_NO = T.USER_NO) JOIN LESSON_ATTACHMENT LA ON(LA.L_NO = L.L_NO) JOIN MEMBER M ON(M.USER_NO = L.USER_NO) JOIN USERPROPIC UPR ON(UPR.USER_NO = L.USER_NO) LEFT JOIN AVG_REVIEW AR ON(AR.L_NO = L.L_NO) LEFT JOIN COUNT_CLASS CC ON(CC.L_NO = L.L_NO) WHERE L.L_NO IN
#{item}
AND L.L_STATUS = 'Y' AND LA.LA_TYPE = 1 AND LS.START_TIME >= SYSDATE AND LS.REMAIN !=0) GROUP BY L_REGION

SELECT L_REGION_SUB as location, COUNT(*)as count, 'RegionSub' as lType FROM LESSON_INFO WHERE L_NO IN ( SELECT L.L_NO FROM LESSON_INFO L JOIN LESSON LS ON(L.L_NO = LS.L_NO) JOIN TUTOR T ON(L.USER_NO = T.USER_NO) JOIN LESSON_ATTACHMENT LA ON(LA.L_NO = L.L_NO) JOIN MEMBER M ON(M.USER_NO = L.USER_NO) JOIN USERPROPIC UPR ON(UPR.USER_NO = L.USER_NO) LEFT JOIN AVG_REVIEW AR ON(AR.L_NO = L.L_NO) LEFT JOIN COUNT_CLASS CC ON(CC.L_NO = L.L_NO) WHERE L.L_NO IN
#{item}
AND L.L_STATUS = 'Y' AND LA.LA_TYPE = 1 AND LS.START_TIME >= SYSDATE AND LS.REMAIN !=0) GROUP BY L_REGION_SUB
