mapper.select:
SELECT count(*)
FROM LESSON_INFO L

 L.L_STATUS = 'Y'

	
	AND (L.L_TITLE LIKE '%' || #{sValue} || '%') OR (L.L_INTRODUCTION LIKE  '%' || #{sValue} || '%')
	
	
	AND L.L_CATE_MAIN = #{cateMain}
	
	
	AND L.L_CATE_SUB = #{cateSub}

	SELECT COUNT(DISTINCT L_NO)
	FROM SEARCH_DETAIL
	
		 L_STATUS = 'Y'
		
			 AND L_DAY IN 
			 
            #{item}
        	 
		
		
			AND (L_TIME LIKE 
			 	 
            '%' || #{item} || '%'      
        	)
		
		
			AND TTYPE = #{tType}
		
		
			AND L_REGION = #{regionMain}
		
		
			AND L_REGION_SUB = #{regionSub}
		
		
			AND (L_TITLE LIKE '%' || #{sValue} || '%') OR (L_INTRODUCTION LIKE  '%' || #{sValue} || '%')
		
		
			AND L_CATE_MAIN = #{cateMain}
		
		
			AND L_CATE_SUB = #{cateSub}

	SELECT DISTINCT L_NO
	FROM SEARCH_DETAIL
	
	 L_STATUS = 'Y'
		
			 AND L_DAY IN 
			 
            #{item}
        	 
		
		
			AND (L_TIME LIKE 
			 	 
            '%' || #{item} || '%'      
        	)
		
		
			AND TTYPE = #{tType}
		
		
			AND L_REGION LIKE  '%' || #{regionMain} || '%'
		
		
			AND L_REGION_SUB = #{regionSub}
		
		
			AND (L_TITLE LIKE '%' || #{sValue} || '%') OR (L_INTRODUCTION LIKE  '%' || #{sValue} || '%')
		
		
			AND L_CATE_MAIN = #{cateMain}
		
		
			AND L_CATE_SUB = #{cateSub}

SELECT L.L_NO, L.L_TITLE, LA.C_NAME, L.PRICE, L.USER_NO, M.USER_NAME, T.T_NICK, U_C_NAME, NVL(AR.AVG_RATING, 0) AS TOTAL_REVIEW_RATING, NVL(C_COUNT,0) AS T_USER_NUMBER ,L.L_REGION_SUB, NVL(ar.l_review_count,0) AS L_REVIEW_COUNT, L.L_CATE_MAIN, L.L_CATE_SUB FROM LESSON_INFO L JOIN TUTOR T ON(L.USER_NO = T.USER_NO) JOIN LESSON_ATTACHMENT LA ON(LA.L_NO = L.L_NO) JOIN MEMBER M ON(M.USER_NO = L.USER_NO) JOIN USERPROPIC UPR ON(UPR.USER_NO = L.USER_NO) LEFT JOIN AVG_REVIEW AR ON(AR.L_NO = L.L_NO) LEFT JOIN COUNT_CLASS CC ON(CC.L_NO = L.L_NO) WHERE L.L_NO IN
#{item}
AND L.L_STATUS = 'Y' AND LA.LA_TYPE = 1 ORDER BY L.L_NO DESC

SELECT L_REGION as location, COUNT(*) as count, 'RegionMain' as lType FROM LESSON_INFO WHERE L_NO IN ( SELECT L.L_NO FROM LESSON_INFO L JOIN TUTOR T ON(L.USER_NO = T.USER_NO) JOIN LESSON_ATTACHMENT LA ON(LA.L_NO = L.L_NO) JOIN MEMBER M ON(M.USER_NO = L.USER_NO) JOIN USERPROPIC UPR ON(UPR.USER_NO = L.USER_NO) LEFT JOIN AVG_REVIEW AR ON(AR.L_NO = L.L_NO) LEFT JOIN COUNT_CLASS CC ON(CC.L_NO = L.L_NO)
AND LA.LA_TYPE = 1 AND L.L_STATUS = 'Y'
AND (L.L_TITLE LIKE '%' || #{sValue} || '%') OR (L.L_INTRODUCTION LIKE '%' || #{sValue} || '%')
AND L.L_CATE_MAIN = #{cateMain}
AND L.L_CATE_SUB = #{cateSub}
)
GROUP BY L_REGION

SELECT L_REGION_SUB as location, COUNT(*)as count, 'RegionSub' as lType FROM LESSON_INFO WHERE L_NO IN ( SELECT L.L_NO FROM LESSON_INFO L JOIN TUTOR T ON(L.USER_NO = T.USER_NO) JOIN LESSON_ATTACHMENT LA ON(LA.L_NO = L.L_NO) JOIN MEMBER M ON(M.USER_NO = L.USER_NO) JOIN USERPROPIC UPR ON(UPR.USER_NO = L.USER_NO) LEFT JOIN AVG_REVIEW AR ON(AR.L_NO = L.L_NO) LEFT JOIN COUNT_CLASS CC ON(CC.L_NO = L.L_NO)
AND LA.LA_TYPE = 1 AND L.L_STATUS = 'Y'
AND (L.L_TITLE LIKE '%' || #{sValue} || '%') OR (L.L_INTRODUCTION LIKE '%' || #{sValue} || '%')
AND L.L_CATE_MAIN = #{cateMain}
AND L.L_CATE_SUB = #{cateSub}
)
GROUP BY L_REGION_SUB

SELECT L_REGION as location, COUNT(*) as count, 'RegionMain' as lType FROM LESSON_INFO WHERE L_NO IN ( SELECT L.L_NO FROM LESSON_INFO L JOIN TUTOR T ON(L.USER_NO = T.USER_NO) JOIN LESSON_ATTACHMENT LA ON(LA.L_NO = L.L_NO) JOIN MEMBER M ON(M.USER_NO = L.USER_NO) JOIN USERPROPIC UPR ON(UPR.USER_NO = L.USER_NO) LEFT JOIN AVG_REVIEW AR ON(AR.L_NO = L.L_NO) LEFT JOIN COUNT_CLASS CC ON(CC.L_NO = L.L_NO) WHERE L.L_NO IN
#{item}
AND L.L_STATUS = 'Y' AND LA.LA_TYPE = 1) GROUP BY L_REGION

SELECT L_REGION_SUB as location, COUNT(*)as count, 'RegionSub' as lType FROM LESSON_INFO WHERE L_NO IN ( SELECT L.L_NO FROM LESSON_INFO L JOIN TUTOR T ON(L.USER_NO = T.USER_NO) JOIN LESSON_ATTACHMENT LA ON(LA.L_NO = L.L_NO) JOIN MEMBER M ON(M.USER_NO = L.USER_NO) JOIN USERPROPIC UPR ON(UPR.USER_NO = L.USER_NO) LEFT JOIN AVG_REVIEW AR ON(AR.L_NO = L.L_NO) LEFT JOIN COUNT_CLASS CC ON(CC.L_NO = L.L_NO) WHERE L.L_NO IN
#{item}
AND L.L_STATUS = 'Y' AND LA.LA_TYPE = 1) GROUP BY L_REGION_SUB
